import{_ as t,c as e,a3 as o,o as r}from"./chunks/framework.CVzIybAD.js";const m=JSON.parse('{"title":"Про СУІБ","description":"","frontmatter":{},"headers":[],"relativePath":"about.md","filePath":"about.md"}'),n={name:"about.md"};function s(i,a,p,l,c,d){return r(),e("div",null,a[0]||(a[0]=[o('<h1 id="про-суіб" tabindex="-1">Про СУІБ <a class="header-anchor" href="#про-суіб" aria-label="Permalink to &quot;Про СУІБ&quot;">​</a></h1><h2 id="що-таке-суіб" tabindex="-1">Що таке СУІБ <a class="header-anchor" href="#що-таке-суіб" aria-label="Permalink to &quot;Що таке СУІБ&quot;">​</a></h2><p>Система управління інформаційною безпекою СУІБ (англ. information security management system, ISMS) — частина загальної системи управління підприємством, яка ґрунтується на підході, що враховує ризики інформаційної безпеки як ризики ведення господарської діяльності, призначена для розроблення, впровадження, функціонування, моніторингу, перегляду, підтримування та вдосконалення інформаційної безпеки.</p><h2 id="стратегічна-ціль-суіб" tabindex="-1">Стратегічна ціль СУІБ <a class="header-anchor" href="#стратегічна-ціль-суіб" aria-label="Permalink to &quot;Стратегічна ціль СУІБ&quot;">​</a></h2><p><strong>Забезпечення конфіденційності інформації</strong> при здійсненні управлінських, технічних, операційних процесів діяльності, а також виконанні функцій та завдань компанії шляхом проведення постійного моніторингу з метою упередження, виявлення та ліквідації загроз (порушень) конфіденційності, зокрема загроз, що пов’язані із захистом від несанкціонованих дій та несанкціонованого доступу до інформації, що можуть здійснюватися шляхом підключення до ІКС та засобів обробки, маскування під зареєстрованого користувача, несанкціонованим ознайомлення зі змістом інформації або компроментації інформації, подолання заходів захисту з метою використання інформації або нав’язування хибної інформації; застосування закладних пристроїв чи програм; використання комп’ютерних вірусів; захисту від несанкціонованого ознайомлення з інформацією під час експорту/імпорту через незахищене середовище, контролю та перекриттям прихованих каналів, а також забезпеченням цілісності комплексу технічних засобів захисту інформації.</p><p><strong>Забезпечення цілісності інформації</strong> при здійсненні управлінських, технічних, операційних процесів діяльності, а також виконанні функцій та завдань компанії шляхом проведення постійного моніторингу з метою упередження, виявлення та ліквідації загроз (порушень) цілісності, зокрема загроз, пов’язаних із несанкціонованою модифікацією інформації та керуванням потоками інформації, а також забезпеченням цілісності комплексу технічних засобів захисту інформації.</p><p><strong>Забезпечення доступності інформації</strong> при здійсненні управлінських, технічних, операційних процесів діяльності, а також виконанні функцій та завдань компанії шляхом проведення постійного моніторингу з метою упередження, виявлення та ліквідації загроз (порушень) доступності інформації, зокрема загроз, пов’язаних з порушеннями використання комп&#39;ютерних систем або оброблюваної інформації, а також загроз стійкість до відмов, відновлення після збоїв і гарантій доступності в процесі заміни окремих елементів системи, а також забезпеченням цілісності комплексу технічних засобів захисту інформації.</p><p><strong>Забезпечення спостережності інформації</strong> при здійсненні управлінських, технічних, операційних процесів діяльності, а також виконанні функцій та завдань компанії шляхом проведення постійного моніторингу з метою упередження, виявлення та ліквідація загроз (порушень) спостережності і технологій обробки інформації та адміністративного керування доступом до інформації з боку об’єктів (процесів керованості), зокрема процесам ідентифікації і контролю за діями користувачів, забезпечення керованості комп&#39;ютерною системою, забезпечення послуг реєстрації (аудиту), ідентифікації і автентифікації, розподілу обов’язків, цілісності комплексу засобів захисту інформації, самотестування, ідентифікація і автентифікація при обміні.</p><p><strong>Здійснення контролю ефективності</strong> при впровадженні системи управління інформаційною безпекою в коипанії.</p>',9)]))}const u=t(n,[["render",s]]);export{m as __pageData,u as default};
