import{_ as a,c as r,a3 as o,o as t}from"./chunks/framework.CVzIybAD.js";const f=JSON.parse('{"title":"Повідомлення про інцидент або порушення","description":"","frontmatter":{"aside":false},"headers":[],"relativePath":"report.md","filePath":"report.md"}'),s={name:"report.md"};function l(n,e,d,p,c,h){return t(),r("div",null,e[0]||(e[0]=[o('<h1 id="повідомлення-про-інцидент-або-порушення" tabindex="-1">Повідомлення про інцидент або порушення <a class="header-anchor" href="#повідомлення-про-інцидент-або-порушення" aria-label="Permalink to &quot;Повідомлення про інцидент або порушення&quot;">​</a></h1><h2 id="офіціине-повідомлення-працівника-про-кіберінцидент-кібератаку-на-ікс-підприємства" tabindex="-1">Офіційне повідомлення працівника про кіберінцидент/кібератаку на ІКС підприємства <a class="header-anchor" href="#офіціине-повідомлення-працівника-про-кіберінцидент-кібератаку-на-ікс-підприємства" aria-label="Permalink to &quot;Офіційне повідомлення працівника про кіберінцидент/кібератаку на ІКС підприємства&quot;">​</a></h2><p>Збір інформації щодо кіберінцидентів є обов&#39;язком працівників компанії, які забезпечують функціонування ІКС підприємства на всіх ланках адміністрування. Це повідомлення буде використано для інформаційного обміну між компанією та суб’єктами забезпечення кібербезпеки під час реагування на кіберінциденти/кібератаки (CERT-UA, ДКБ СБУ, НКЦК) відповідно до ПОРЯДКУ взаємодії суб’єктів забезпечення кібербезпеки під час реагування на кіберінциденти/кібератаки, затвердженим РНБО України.</p><p><a href="https://docs.google.com/forms/d/e/1FAIpQLSd2Fm3CeBAoDhaT7bmFtVGO2y_OSOsM69DugYlyf-f9Gskt7w/viewform" target="_blank" rel="noreferrer">Повідомити про кіберінцидент/кібератаку</a></p><h2 id="анонімне-повідомлення-про-порушення-інформаціиноі-безпеки" tabindex="-1">Анонімне повідомлення про порушення інформаційної безпеки <a class="header-anchor" href="#анонімне-повідомлення-про-порушення-інформаціиноі-безпеки" aria-label="Permalink to &quot;Анонімне повідомлення про порушення інформаційної безпеки&quot;">​</a></h2><p>У разі виявлення порушень конфіденційності, цілісності чи доступності інформації під час її обробки в інформаційно-комунікаційних системах підприємства, а також при виявленні невідповідностей вимогам документів СУІБ, будь ласка, скористайтеся кнопкою для доступу до форми анонімного повідомлення. Це повідомлення буде використано для усунення недоліків системи та її вдосконалення.</p><p><a href="https://docs.google.com/forms/d/e/1FAIpQLSfH5P3DB_dHh8NcJ6fQOAcvmGzvnoI0A8ZSvOonFr48ub4RuQ/viewform" target="_blank" rel="noreferrer">Повідомити про порушення ІБ</a></p><h2 id="анонімне-повідомлення-про-порушення-захисту-персональних-даних" tabindex="-1">Анонімне повідомлення про порушення захисту персональних даних <a class="header-anchor" href="#анонімне-повідомлення-про-порушення-захисту-персональних-даних" aria-label="Permalink to &quot;Анонімне повідомлення про порушення захисту персональних даних&quot;">​</a></h2><p>Згідно вимог статті 24 Закону України «Про захист персональних даних», працівники Компанії зобов’язані забезпечити захист цих даних від випадкових втрати або знищення, від незаконної обробки, у тому числі незаконного знищення чи доступу до персональних даних. У разі виявлення таких порушень, будь ласка, скористайтеся кнопкою для доступу до форми анонімного повідомлення.</p><p><a href="https://docs.google.com/forms/d/e/1FAIpQLScDmR_KkIc4yLpelxbub6kC9_923doBm7YKLbh2z588S4dwRw/viewform" target="_blank" rel="noreferrer">Повідомити про порушення обробки ПД</a></p>',10)]))}const m=a(s,[["render",l]]);export{f as __pageData,m as default};
